variables.input:
timeseries avg(otelcol_process_uptime), by: {dynatrace.otel.collector}
| filterOut isNull(dynatrace.otel.collector)
| fields dynatrace.otel.collector

24.query:
timeseries total=avg(otelcol_process_runtime_heap_alloc_bytes), by: {k8s.cluster.name,dynatrace.otel.collector}
| filter in(k8s.cluster.name,$cluster)
| filter in(dynatrace.otel.collector,$collector)

29.query:
timeseries cpu = avg(k8s.pod.cpu.utilization), by: {k8s.cluster.name,k8s.namespace.name,k8s.pod.name}
| filter in(k8s.cluster.name,$cluster)
| filter k8s.namespace.name == "dynatrace"

30.query:
timeseries mem=avg(k8s.pod.memory.usage), by:{k8s.namespace.name,k8s.pod.name,k8s.cluster.name}
| filter in(k8s.cluster.name,$cluster)
| filter k8s.namespace.name == "dynatrace"

37.query:
timeseries avg(otelcol_process_uptime), by: {k8s.cluster.name,dynatrace.otel.collector,service_name,service_instance_id}
| filter in(k8s.cluster.name,$cluster)
| filter in(dynatrace.otel.collector,$collector)
| summarize Collectors = count(), by: {service_name}

55.query:
timeseries total=sum(otelcol_process_uptime), by: {k8s.cluster.name,dynatrace.otel.collector}
| filter in(k8s.cluster.name,$cluster)
| filter in(dynatrace.otel.collector,$collector)
